// --- Mock Data ---

type User = {
    id: string;
    name: string;
    avatar: string; // URL or initials
};

type Message = {
    id: string;
    sender: User;
    content: string; // For text or caption
    timestamp: string;
    type: 'text' | 'image' | 'video';
    mediaUrl?: string; // For image/video URLs
    timestampValue: number;
};

type Chat = {
    id: string;
    participants: User[];
    messages: Message[];
};

type Comment = {
    id: string;
    author: User;
    content: string;
    timestamp: string;
};

type PrayerRequest = {
    id: string;
    author: User;
    title: string;
    content: string;
    image?: string;
    prayCount: number;
    comments: Comment[];
    isPrayedByCurrentUser: boolean;
    createdAt: string;
};

type NewsItem = {
    id: string;
    title: string;
    date: string;
    content: string;
    image?: string;
};

type Verse = {
    verse: string;
    text: string;
};

type Notification = {
    id: string;
    icon: string; // material symbol name
    message: string;
    timestamp: string;
};

