const ChatListPage = ({ chats, onSelectChat, onCreateChat }: { chats: Chat[]; onSelectChat: (id: string) => void; onCreateChat: () => void; }) => {
    const sortedChats = [...chats].sort((a, b) => {
        const lastMessageA = a.messages.length > 0 ? a.messages[a.messages.length - 1] : null;
        const lastMessageB = b.messages.length > 0 ? b.messages[b.messages.length - 1] : null;

        if (!lastMessageA && !lastMessageB) return 0;
        if (!lastMessageA) return 1; // Chats with no messages go to the bottom
        if (!lastMessageB) return -1;

        return lastMessageB.timestampValue - lastMessageA.timestampValue;
    });
    
    return (
        <div className="page-content chat-list-page">
            <h2>Fellowship</h2>
            <div className="list-container">
                {sortedChats.map(chat => {
                    const otherParticipant = chat.participants.find(p => p.id !== CURRENT_USER.id);
                    if (!otherParticipant) return null;
                    const lastMessage = chat.messages[chat.messages.length - 1];

                    return (
                        <div key={chat.id} className="list-item chat-item" onClick={() => onSelectChat(chat.id)}>
                            <div className="chat-avatar">{otherParticipant.avatar}</div>
                            <div className="chat-info">
                                <span className="chat-name">{otherParticipant.name}</span>
                                <span className="chat-last-message">{lastMessage ? lastMessage.content : 'No messages yet.'}</span>
                            </div>
                        </div>
                    );
                })}
            </div>
            <button className="fab" onClick={onCreateChat} aria-label="New Chat">
                 <span className="material-symbols-outlined">edit</span>
            </button>
        </div>
    );
};

const ConversationPage = ({ chat, onBack, onSendMessage }: { 
    chat: Chat; 
    onBack: () => void; 
    onSendMessage: (content: string, mediaUrl: string | null, type: 'text' | 'image' | 'video') => void; 
}) => {
    const [newMessage, setNewMessage] = React.useState('');
    const [mediaPreview, setMediaPreview] = React.useState<{ url: string; type: 'image' | 'video' } | null>(null);
    const messagesEndRef = React.useRef<HTMLDivElement>(null);
    const fileInputRef = React.useRef<HTMLInputElement>(null);
    const otherParticipant = chat.participants.find(p => p.id !== CURRENT_USER.id);

    React.useEffect(() => {
        messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    }, [chat.messages]);
    
    const handleFileSelect = (e: React.ChangeEvent<HTMLInputElement>) => {
        const file = e.target.files?.[0];
        if (file) {
            const reader = new FileReader();
            reader.onload = (event) => {
                const url = event.target?.result as string;
                const type = file.type.startsWith('image/') ? 'image' : 'video';
                setMediaPreview({ url, type });
            };
            reader.readAsDataURL(file);
        }
    };

    const cancelMedia = () => {
        setMediaPreview(null);
        if (fileInputRef.current) {
            fileInputRef.current.value = '';
        }
    };

    const handleSend = () => {
        if (mediaPreview) {
            onSendMessage(newMessage.trim(), mediaPreview.url, mediaPreview.type);
            setNewMessage('');
            cancelMedia();
        } else if (newMessage.trim()) {
            onSendMessage(newMessage.trim(), null, 'text');
            setNewMessage('');
        }
    };
    
    const handleKeyPress = (e: React.KeyboardEvent) => {
        if (e.key === 'Enter' && !e.shiftKey) {
            e.preventDefault();
            handleSend();
        }
    };

    return (
        <div className="conversation-page">
            <header className="conversation-header">
                <button className="back-button" onClick={onBack}>
                    <span className="material-symbols-outlined">arrow_back</span>
                </button>
                <h3>{otherParticipant?.name}</h3>
                <div style={{width: 40}}></div> {/* Spacer */}
            </header>
            <div className="message-list">
                {chat.messages.map(msg => (
                    <div key={msg.id} className={`message-bubble ${msg.sender.id === CURRENT_USER.id ? 'sent' : 'received'}`}>
                        {msg.type === 'image' && msg.mediaUrl && <img src={msg.mediaUrl} alt="" className="message-image" />}
                        {msg.type === 'video' && msg.mediaUrl && <video src={msg.mediaUrl} controls className="message-video" />}
                        {msg.content && <p className="message-text">{msg.content}</p>}
                        <span className="message-timestamp">{msg.timestamp}</span>
                    </div>
                ))}
                <div ref={messagesEndRef} />
            </div>
            <div className="message-input-area">
                {mediaPreview && (
                    <div className="media-preview-container">
                        {mediaPreview.type === 'image' ? (
                            <img src={mediaPreview.url} alt="Preview" />
                        ) : (
                            <video src={mediaPreview.url} muted />
                        )}
                        <button onClick={cancelMedia} className="cancel-media-button" aria-label="Cancel media selection">
                            <span className="material-symbols-outlined">close</span>
                        </button>
                    </div>
                )}
                <div className="message-input-container">
                     <input type="file" ref={fileInputRef} onChange={handleFileSelect} style={{ display: 'none' }} accept="image/*,video/*" />
                     <button className="input-action-button" onClick={() => fileInputRef.current?.click()} aria-label="Attach file">
                        <span className="material-symbols-outlined">add</span>
                    </button>
                     <input 
                        type="text" 
                        placeholder={mediaPreview ? "Add a caption..." : "Type a message..."}
                        value={newMessage}
                        onChange={(e) => setNewMessage(e.target.value)}
                        onKeyPress={handleKeyPress}
                    />
                     <button className="send-button" onClick={handleSend} aria-label="Send message">
                        <span className="material-symbols-outlined">send</span>
                    </button>
                </div>
            </div>
        </div>
    );
};

