// --- Mock Data ---

type User = {
    id: string;
    name: string;
    avatar: string; // URL or initials
    role: 'admin' | 'member';
};

type Message = {
    id: string;
    sender: User;
    content: string;
    timestamp: string;
    type: 'text' | 'image' | 'video';
    timestampValue: number;
};

type Chat = {
    id: string;
    participants: User[];
    messages: Message[];
};

type Comment = {
    id: string;
    author: User;
    content: string;
    timestamp: string;
};

type PrayerRequest = {
    id: string;
    author: User;
    title: string;
    content: string;
    image?: string;
    prayCount: number;
    comments: Comment[];
    isPrayedByCurrentUser: boolean;
    createdAt: string;
};

type NewsItem = {
    id: string;
    title: string;
    date: string;
    content: string;
    image?: string;
};

type WorshipVOD = {
    id: string;
    title: string;
    youtubeUrl: string; // Should be the embed URL
    date: string;
};

type Podcast = {
    id: string;
    title: string;
    audioUrl: string;
    duration: string;
    createdAt: string;
};


type Verse = {
    verse: string;
    text: string;
};

type Notification = {
    id: string;
    icon: string; // material symbol name
    message: string;
    timestamp: string;
};

