// --- Login/Selection Pages ---
const LoginPage = ({ church, onLogin }: { church: Church; onLogin: (user: User) => void; }) => {
    const [name, setName] = React.useState('');

    const handleLogin = (e: React.FormEvent) => {
        e.preventDefault();
        if (name.trim() === '') return;
        
        const avatar = name.trim().split(' ').map(n => n[0]).join('').toUpperCase();
        
        let role: 'admin' | 'member' = 'member';
        if (name.trim().toLowerCase() === 'admin') {
            role = 'admin';
        }

        const user: User = {
            id: 'user1', // Temp ID, will be reassigned in handleLogin logic
            name: name.trim(),
            avatar: avatar || '?',
            role: role
        };
        onLogin(user);
    };

    return (
        <div className="login-container">
            <div className="login-box">
                <img src={church.logo} alt={`${church.name} Logo`} className="login-logo" />
                <h2>{church.name}</h2>
                <p>Login to join the community.</p>
                <form onSubmit={handleLogin}>
                    <input 
                        className="login-input" 
                        type="text" 
                        placeholder="तपाईको नाम लेख्नुहोस्" 
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        required
                    />
                    <button className="login-button" type="submit">Login</button>
                </form>
            </div>
        </div>
    );
};

