const PodcastPage = ({ podcasts, setPodcasts, canAddPodcast }: { podcasts: Podcast[]; setPodcasts: React.Dispatch<React.SetStateAction<Podcast[]>>; canAddPodcast: boolean; }) => {
    const [isRecording, setIsRecording] = React.useState(false);
    const [audioURL, setAudioURL] = React.useState('');
    const [showSaveModal, setShowSaveModal] = React.useState(false);
    const [newPodcastBlob, setNewPodcastBlob] = React.useState<Blob | null>(null);

    const mediaRecorderRef = React.useRef<MediaRecorder | null>(null);
    const audioChunksRef = React.useRef<Blob[]>([]);
    const fileInputRef = React.useRef<HTMLInputElement>(null);

    const handleStartRecording = async () => {
        try {
            const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
            const mediaRecorder = new MediaRecorder(stream);
            mediaRecorderRef.current = mediaRecorder;

            mediaRecorder.ondataavailable = event => {
                audioChunksRef.current.push(event.data);
            };

            mediaRecorder.onstop = () => {
                const audioBlob = new Blob(audioChunksRef.current, { type: 'audio/wav' });
                const audioUrl = URL.createObjectURL(audioBlob);
                setNewPodcastBlob(audioBlob);
                setAudioURL(audioUrl); // for preview
                setShowSaveModal(true);
                audioChunksRef.current = [];
                stream.getTracks().forEach(track => track.stop());
            };

            audioChunksRef.current = [];
            mediaRecorder.start();
            setIsRecording(true);
        } catch (err) {
            console.error("Error starting recording:", err);
            alert("Could not start recording. Please ensure microphone permissions are granted.");
        }
    };

    const handleStopRecording = () => {
        if (mediaRecorderRef.current) {
            mediaRecorderRef.current.stop();
            setIsRecording(false);
        }
    };

    const handleSavePodcast = (title: string) => {
        if (!newPodcastBlob) return;
        const audioUrl = URL.createObjectURL(newPodcastBlob);
        const newPodcast: Podcast = {
            id: `pod${Date.now()}`,
            title,
            audioUrl: audioUrl,
            duration: 'N/A',
            createdAt: new Date().toISOString()
        };
        setPodcasts(prev => [newPodcast, ...prev]);
        setShowSaveModal(false);
        setNewPodcastBlob(null);
        setAudioURL('');
    };
    
    const handleFileUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
        const file = event.target.files?.[0];
        if (file) {
            setNewPodcastBlob(file);
            const url = URL.createObjectURL(file);
            setAudioURL(url);
            setShowSaveModal(true);
            // Reset file input value to allow uploading the same file again
            if (event.target) {
                event.target.value = '';
            }
        }
    };

    const sortedPodcasts = [...podcasts].sort((a, b) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime());

    return (
        <div className="page-content">
            <h2>Podcast</h2>
            {canAddPodcast && (
                 <div className="card">
                    <h3>Record or Upload</h3>
                    <div className="podcast-controls">
                        <button className="action-button" onClick={isRecording ? handleStopRecording : handleStartRecording} aria-label={isRecording ? 'Stop recording' : 'Start recording'}>
                            <span className="material-symbols-outlined">{isRecording ? 'stop' : 'mic'}</span>
                            {isRecording ? 'Stop Recording' : 'Record Live'}
                        </button>
                        <input type="file" accept="audio/*" ref={fileInputRef} style={{display: 'none'}} onChange={handleFileUpload} />
                        <button className="action-button" onClick={() => fileInputRef.current?.click()} aria-label="Upload audio file">
                            <span className="material-symbols-outlined">upload_file</span>
                            Upload Audio
                        </button>
                    </div>
                    {isRecording && <p className="recording-indicator">Recording...</p>}
                </div>
            )}

            <div className="list-container">
                {sortedPodcasts.map(pod => (
                    <div key={pod.id} className="card podcast-item">
                        <p className="podcast-title">{pod.title}</p>
                        <audio controls src={pod.audioUrl} className="podcast-player"></audio>
                    </div>
                ))}
            </div>

            {showSaveModal && (
                <SavePodcastModal
                    onClose={() => {
                        setShowSaveModal(false);
                        setAudioURL('');
                        setNewPodcastBlob(null);
                    }}
                    onSave={handleSavePodcast}
                    audioPreviewUrl={audioURL}
                />
            )}
        </div>
    );
};

